import React from 'react';
import Card from './components/Card';
import Form from './components/Form';

class App extends React.Component {
  state = {
    cardName: '',
    cardDescription: '',
    cardAttr1: 0,
    cardAttr2: 0,
    cardAttr3: 0,
    cardImage: '',
    cardRare: '',
    cardTrunfo: false,
    isSaveButtonDisabled: true,
  };

  onInputChange = ({ target }) => {
    const { name } = target;
    const validationNumber = 90;
    const value = target.type === 'radio' ? target.checked : target.value;
    if ((name === 'cardAttr1' || name === 'cardAttr2' || name === 'cardAttr3')
    && +target.value > validationNumber) {
      return this.setState({
        isSaveButtonDisabled: true,
      });
    }
    this.setState({ [name]: value }, () => {
      const { cardName, cardDescription, cardImage, cardRare } = this.state;
      const { cardAttr1, cardAttr2, cardAttr3 } = this.state;
      const numberValid = 210;
      const newStatusButton = cardName === ''
      || cardDescription === ''
      || cardImage === ''
      || cardRare === ''
      || (+cardAttr1 + +cardAttr2 + +cardAttr3) > numberValid;
      this.setState({
        isSaveButtonDisabled: newStatusButton,
      });
    });
  };

  render() {
    const { cardName, cardDescription, cardAttr1, cardAttr2 } = this.state;
    const { cardAttr3, cardImage, cardRare, cardTrunfo } = this.state;
    const { isSaveButtonDisabled } = this.state;
    return (
      <div>
        <h1>Tryunfo.</h1>
        <Form
          cardTrunfo={ cardTrunfo }
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
          isSaveButtonDisabled={ isSaveButtonDisabled }
          onInputChange={ this.onInputChange }
          onSaveButtonClick={ this.onSaveButtonClick }
        />
        <Card
          cardTrunfo={ cardTrunfo }
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardImage={ cardImage }
          cardRare={ cardRare }
        />
      </div>
    );
  }
}

export default App;
